def calculate_distance(distance, height_difference):    return (distance ** 2 + height_difference ** 2) ** 0.5def define_max_wire_length(gap_distance, heights):    max_top = 0    max_low = 0    index = 0    while index < len(heights) - 1:        height_diff = abs(heights[index] - heights[index + 1])        top_low = max_top + calculate_distance(gap_distance, heights[index] - 1)        low_low = max_low + gap_distance        low_top = max_low + calculate_distance(gap_distance, heights[index + 1] - 1)        top_top = max_top + calculate_distance(gap_distance, height_diff)        max_top = max(low_top, top_top)        max_low = max(low_low, top_low)        index += 1    return max(max_top, max_low)def read_txt():    file_path = 'electro_input.txt'    with open(file_path, 'r') as file:        distance_between_poles = int(file.readline())        heights = [int(x) for x in file.readline().split()]        return distance_between_poles, heightsdef write_txt(result):    file_path = 'electro_output.txt'    with open(file_path, 'w') as file:        file.write(f"{result:.2f}")distance, heights = read_txt()max_wire_length = define_max_wire_length(distance, heights)write_txt(max_wire_length)